#!/bin/sh
#This is a sample wrapper script form generated by EasyPBI
# REMINDER: Add this file to the external-links for the PBI as well!

#Setup some internal script variables
PROGDIR=`pwd | cut -d / -f 1-4`   #Base PBI directory (/usr/pbi/myapplication-<arch>)
APPBINARY=bin/pacpl   #application binary

DIR=`find ${PROGDIR} | grep Switch.pm`
export PERL5LIB=${PERL5LIB}:`dirname ${DIR}`

#Now start the main application
